import { Suspense } from 'react';
import { notFound } from 'next/navigation';
import { createClient } from '@/lib/supabase/server';
import VideoPreview from '@/components/VideoPreview';
import ThumbnailOptions from '@/components/ThumbnailOptions';
import ScriptSection from '@/components/ScriptSection';
import SEOSection from '@/components/SEOSection';

export default async function ProjectDetailPage({ params }: { params: { id: string } }) {
  const supabase = createClient();

  const { data: project, error } = await supabase
    .from('projects')
    .select('*')
    .eq('id', params.id)
    .single();

  if (error || !project) {
    notFound();
  }

  return (
    <div className="container mx-auto p-6">
      <header className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold">{project.title}</h1>
          <span className={`badge ${project.status === 'complete' ? 'badge-success' : 'badge-draft'}`}>
            {project.status}
          </span>
        </div>
        <div className="flex gap-2">
          <DownloadVideoButton projectId={params.id} />
          <CopyYouTubeDataButton projectId={params.id} />
        </div>
      </header>

      <Suspense fallback={<VideoSkeleton />}>
        <VideoPreview projectId={params.id} />
      </Suspense>

      <ThumbnailOptions projectId={params.id} />
      <ScriptSection projectId={params.id} />
      <SEOSection projectId={params.id} />
    </div>
  );
}
